import React from "react";
import { NavLink } from "react-router-dom";
import classes from "./Dialogs.module.css";

const DialogItem = (props) => {
  let path = "/dialogs/" + props.id;

  return (
    <div className={classes.dialog + " " + classes.active}>
      <NavLink to={path}> {props.name}</NavLink>
    </div>
  );
};

const message = (props) => {
  return <div className={classes.message}>{props.message}</div>;
};

const Dialogs = (props) => {
  let dialogsData = [
    {
      id: 1,
      name: "Sasha",
    },
    {
      id: 2,
      name: "Oleg",
    },
    {
      id: 3,
      name: "Ivan",
    },
    {
      id: 4,
      name: "Anna",
    },
    {
      id: 5,
      name: "Viktor",
    },
  ];

  let dialogsElements = dialogsData.map((dialog) => (
    <DialogItem name={dialog.name} id={dialog.id} />
  ));


  let messagesData = [
    {
      id: 1,
      message: "Hi",
    },
    {
      id: 2,
      message: "How is your progect",
    },
    {
      id: 3,
      message: "Yo",
    },
    {
      id: 4,
      message: "yooo",
    },
    {
      id: 5,
      message: "",
    },
  ];
  let messagesElements = messagesData.map(massage =>  <Messag message={massage.message} />)

  return (
    <div className={classes.dialogs}>
      <div className={classes.dialogs_item}>
        {dialogsElements}
        {/* <DialogItem name={dialogsData[0].name} id={dialogsData[0].id} />
        <DialogItem name={dialogsData[1].name} id={dialogsData[1].id} />
        <DialogItem name={dialogsData[2].name} id={dialogsData[2].id} />
        <DialogItem name={dialogsData[3].name} id={dialogsData[3].id} />
        <DialogItem name={dialogsData[4].name} id={dialogsData[4].id} /> */}
      </div>
      <div className={classes.messages}>
        <Message message={messagesData[0].message} />
        <Messag message={messagesData[1].message} />
        <Messag message={messagesData[2].message} />
        <Messag message={messagesData[3].message} />
        <Messag message="" />
      </div>
    </div>
  );
};

export default Dialogs;
